import React, { useState } from 'react';
import './App.css';

function App() {
    const [ticker, setTicker] = useState('');
    const [selectedRange, setSelectedRange] = useState('6M');
    const [analysisData, setAnalysisData] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const convertRangeToDays = (range) => {
        const rangeMap = {
            '1M': 30,
            '3M': 90,
            '6M': 180,
            '1Y': 365,
            '5Y': 1825
        };
        return rangeMap[range] || 180;
    };

    const fetchAnalysisFromBackend = async (stockSymbol, range) => {
        setLoading(true);
        setError('');
        setAnalysisData(null);

        try {
            const days = convertRangeToDays(range);
            const response = await fetch(`http://localhost:8000/analyze?symbol=${stockSymbol}&days=${days}`);

            if (!response.ok) {
                throw new Error(`Analysis failed: ${response.statusText}`);
            }

            const data = await response.json();
            setAnalysisData(data);
        } catch (err) {
            console.error('Analysis request failed:', err);
            setError(err.message || 'Failed to fetch analysis');
        } finally {
            setLoading(false);
        }
    };

    const handleAnalyzeClick = () => {
        if (ticker.trim()) {
            fetchAnalysisFromBackend(ticker.trim().toUpperCase(), selectedRange);
        }
    };

    const handleRangeSelection = (newRange) => {
        setSelectedRange(newRange);
        if (analysisData && ticker.trim()) {
            fetchAnalysisFromBackend(ticker.trim().toUpperCase(), newRange);
        }
    };

    const calculateRecommendation = () => {
        if (!analysisData?.sentimentCounts) return null;

        const { positive = 0, negative = 0, neutral = 0 } = analysisData.sentimentCounts;
        const total = positive + negative + neutral;

        if (total === 0) return null;

        if (positive > negative) {
            return { text: 'BUY', color: '#00ff88', confidence: 'Strong' };
        } else if (negative > positive) {
            return { text: 'AVOID', color: '#ff4757', confidence: 'Caution' };
        } else {
            return { text: 'HOLD', color: '#ffa502', confidence: 'Neutral' };
        }
    };

    const recommendation = calculateRecommendation();

    return (
        <div className="app">
            <div className="hero-section">
                <div className="nav">
                    <div className="logo">RhymeWatch</div>
                    <div className="nav-links">
                        <span>Analysis</span>
                        <span>Markets</span>
                        <span>About</span>
                    </div>
                </div>

                <div className="hero-content">
                    <h1 className="hero-title">
                        Decode Market
                        <span className="gradient-text"> Sentiment</span>
                        <br />
                        Before It Moves
                    </h1>
                    <p className="hero-subtitle">
                        Advanced AI sentiment analysis meets real-time market intelligence.
                        Make informed investment decisions with machine learning predictions.
                    </p>

                    <div className="analysis-card">
                        <div className="input-section">
                            <div className="input-group">
                                <label>Stock Symbol</label>
                                <input
                                    type="text"
                                    value={ticker}
                                    onChange={(e) => setTicker(e.target.value.toUpperCase())}
                                    placeholder="AAPL, TSLA, MSFT..."
                                    className="stock-input"
                                    onKeyPress={(e) => e.key === 'Enter' && handleAnalyzeClick()}
                                />
                            </div>
                            <div className="range-group">
                                <label>Time Range</label>
                                <select
                                    value={selectedRange}
                                    onChange={(e) => handleRangeSelection(e.target.value)}
                                    className="range-select"
                                >
                                    <option value="1M">1 Month</option>
                                    <option value="3M">3 Months</option>
                                    <option value="6M">6 Months</option>
                                    <option value="1Y">1 Year</option>
                                    <option value="5Y">5 Years</option>
                                </select>
                            </div>
                            <button
                                onClick={handleAnalyzeClick}
                                disabled={loading || !ticker.trim()}
                                className="analyze-btn"
                            >
                                {loading ? (
                                    <span className="loading-text">Analyzing...</span>
                                ) : (
                                    'Analyze Sentiment'
                                )}
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            {error && (
                <div className="error-container">
                    <div className="error-card">
                        <div className="error-icon">⚠️</div>
                        <div className="error-content">
                            <h3>Analysis Error</h3>
                            <p>{error}</p>
                        </div>
                    </div>
                </div>
            )}

            {analysisData && (
                <div className="results-section">
                    <div className="results-header">
                        <h2>Market Intelligence for <span className="ticker-highlight">{ticker}</span></h2>
                        <p>Based on {analysisData.total_headlines} recent headlines</p>
                    </div>

                    <div className="metrics-grid">
                        <div className="metric-card sentiment-card">
                            <h3>Sentiment Distribution</h3>
                            <div className="sentiment-bars">
                                <div className="sentiment-bar">
                                    <div className="sentiment-label">
                                        <span>Positive</span>
                                        <span className="sentiment-value">{analysisData.sentimentCounts.positive}</span>
                                    </div>
                                    <div className="bar-container">
                                        <div
                                            className="bar positive-bar"
                                            style={{
                                                width: `${(analysisData.sentimentCounts.positive / analysisData.total_headlines) * 100}%`
                                            }}
                                        ></div>
                                    </div>
                                </div>
                                <div className="sentiment-bar">
                                    <div className="sentiment-label">
                                        <span>Neutral</span>
                                        <span className="sentiment-value">{analysisData.sentimentCounts.neutral}</span>
                                    </div>
                                    <div className="bar-container">
                                        <div
                                            className="bar neutral-bar"
                                            style={{
                                                width: `${(analysisData.sentimentCounts.neutral / analysisData.total_headlines) * 100}%`
                                            }}
                                        ></div>
                                    </div>
                                </div>
                                <div className="sentiment-bar">
                                    <div className="sentiment-label">
                                        <span>Negative</span>
                                        <span className="sentiment-value">{analysisData.sentimentCounts.negative}</span>
                                    </div>
                                    <div className="bar-container">
                                        <div
                                            className="bar negative-bar"
                                            style={{
                                                width: `${(analysisData.sentimentCounts.negative / analysisData.total_headlines) * 100}%`
                                            }}
                                        ></div>
                                    </div>
                                </div>
                            </div>
                        </div>

                        {recommendation && (
                            <div className="metric-card recommendation-card">
                                <h3>Investment Signal</h3>
                                <div className="recommendation-content">
                                    <div className="recommendation-badge" style={{ color: recommendation.color }}>
                                        {recommendation.text}
                                    </div>
                                    <div className="confidence-level">
                                        {recommendation.confidence} Signal
                                    </div>
                                    <p className="recommendation-desc">
                                        Based on sentiment analysis of recent market coverage
                                    </p>
                                </div>
                            </div>
                        )}

                        {analysisData.nextDayUp !== null && (
                            <div className="metric-card prediction-card">
                                <h3>ML Prediction</h3>
                                <div className="prediction-content">
                                    <div className={`prediction-indicator ${analysisData.nextDayUp ? 'up' : 'down'}`}>
                                        <span className="prediction-arrow">
                                            {analysisData.nextDayUp ? '↗' : '↘'}
                                        </span>
                                        <span className="prediction-text">
                                            {analysisData.nextDayUp ? 'Upward' : 'Downward'}
                                        </span>
                                    </div>
                                    <p className="prediction-desc">
                                        Next-day movement probability
                                    </p>
                                </div>
                            </div>
                        )}
                    </div>

                    {analysisData.news && analysisData.news.length > 0 && (
                        <div className="news-section">
                            <h3>Recent Headlines</h3>
                            <div className="news-grid">
                                {analysisData.news.slice(0, 8).map((item, index) => (
                                    <div key={index} className={`news-card ${item.sentiment}`}>
                                        <div className="news-content">
                                            <h4>{item.headline}</h4>
                                            <div className="news-meta">
                                                <span className="news-date">{item.date}</span>
                                                <span className={`sentiment-tag ${item.sentiment}`}>
                                                    {item.sentiment}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                </div>
            )}

            <footer className="footer">
                <div className="footer-content">
                    <div className="footer-text">
                        <p>RhymeWatch - AI-Powered Market Intelligence</p>
                        <p className="disclaimer">Not financial advice. For educational purposes only.</p>
                    </div>
                </div>
            </footer>
        </div>
    );
}

export default App;
