// stockTickers.js - Load stock tickers from JSON file

export const loadStockTickers = async () => {
    try {
        // Option 1: Load from a local JSON file
        const response = await fetch('/stockTickers.json');
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('Failed to load stock tickers:', error);
        // Fallback to a default set
        return DEFAULT_TICKERS;
    }
};

// Default tickers as fallback
const DEFAULT_TICKERS = [
    { symbol: 'AAPL', name: 'Apple Inc.', sector: 'Technology' },
    { symbol: 'GOOGL', name: 'Alphabet Inc.', sector: 'Technology' },
    { symbol: 'MSFT', name: 'Microsoft Corporation', sector: 'Technology' },
    { symbol: 'AMZN', name: 'Amazon.com Inc.', sector: 'Consumer Discretionary' },
    { symbol: 'TSLA', name: 'Tesla Inc.', sector: 'Consumer Discretionary' },
    { symbol: 'META', name: 'Meta Platforms Inc.', sector: 'Technology' },
    { symbol: 'NVDA', name: 'NVIDIA Corporation', sector: 'Technology' },
    { symbol: 'JPM', name: 'JPMorgan Chase & Co.', sector: 'Financials' },
    { symbol: 'V', name: 'Visa Inc.', sector: 'Financials' },
    { symbol: 'WMT', name: 'Walmart Inc.', sector: 'Consumer Staples' },
];
